mapper.select:
SELECT B.* FROM ( SELECT A.* , ROWNUM RN FROM ( select l.*, case l.svc_stts when 1 then '접수 완료' when 2 then '서비스 완료' else '취소 및 환불' end as status, c.name, case s.knd when 1 then '돌봄 서비스' when 2 then '산책 서비스' else '훈련 서비스' end as knd_name, s.term, s.prc, ( select count(*) from review r where r.reser_no = l.reser_no ) as reviewyn
from reser_list l, svc s, client c where l.client_id2 = c.client_id and s.svc_id = l.svc_id and l.client_id1 = #{client_id1}
) A ) B WHERE RN >= #{start} and RN <= #{end}
